INNER JOIN clients ON customers.customer = clients.customer;
#Query que nos dice cuando obtuvimos los datos del cliente y cuando se suscribio a el gimm


SELECT clients.type_payment AS `Tipo de pago`,COUNT(clients.type_payment) as Max  from types_payments as `pago`
INNER JOIN clients  ON clients.type_payment = pago.type_payment
GROUP BY clients.type_payment
ORDER BY Max DESC; #El numero maximo y el tipo de venta

SELECT clients.type_payment AS `Tipo de pago`,COUNT(clients.type_payment) as Max  from types_payments as `pago`
INNER JOIN clients  ON clients.type_payment = pago.type_payment
GROUP BY clients.type_payment
ORDER BY `Tipo de pago`; #Numero de clientes por numero de ventas

SELECT coachs.coach `N.M`, `name` Profesor,COUNT(coachs.coach) AS Grupos from coachs
inner join classes on classes.coach = coachs.coach
GROUP BY coachs.coach
ORDER BY coachs.coach;
#Grupos de cada profesor
SELECT coachs.coach `N.M`, `name` Profesor,COUNT(coachs.coach) AS Grupos from coachs
inner join classes on classes.coach = coachs.coach
GROUP BY coachs.coach
ORDER BY Grupos DESC;
